mapper.update:
update dingtalk_user set `name` = #{name,jdbcType=VARCHAR}, `position` = #{position,jdbcType=VARCHAR}, mobile = #{mobile,jdbcType=VARCHAR}, tel = #{tel,jdbcType=VARCHAR}, workPlace = #{workplace,jdbcType=VARCHAR}, remark = #{remark,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, orgEmail = #{orgemail,jdbcType=VARCHAR},
headPic = #{headPic,jdbcType=VARCHAR},
jobnumber = #{jobnumber,jdbcType=VARCHAR} where userid = #{userid,jdbcType=INTEGER}

update dingtalk_user
set `name` = #{name,jdbcType=VARCHAR},
email = #{email,jdbcType=VARCHAR},
head_pic = #{headPic,jdbcType=VARCHAR}
where userid = #{userid,jdbcType=INTEGER}

update dingtalk_user
set password = #{1}
where userid = #{0}

mapper.delete:
delete from dingtalk_user where userid in
#{item}
and is_admin =0

mapper.select:
select  from dingtalk_user where 1=1

  and userid=#{userid}


  and name=#{name}


  and position=#{position}


  and mobile=#{mobile}


  and tel=#{tel}


  and workPlace=#{workplace}


  and remark=#{remark}


  and email=#{email}


  and orgEmail=#{orgemail}


  and jobnumber=#{jobnumber}


  and isHide=#{ishide}


  and isSenior=#{issenior}


  and intermediary_id=#{intermediaryId}


  and create_time=#{createTime}


  and update_time=#{updateTime}


  and is_admin=#{isAdmin}


  and password=#{password}


  and extattr=#{extattr}


  order by ${od}

select
,
from dingtalk_user a,dingtalk_user_dept b where a.userid=b.user_id and b.dept_id=#{0}
