mapper.delete:
delete from intermediary
where intermediary_id in

          #{item}

mapper.select:
select i.*,u.name uname,u.mobile,u.userid,a.*
FROM intermediary i
LEFT JOIN area a
ON i.area_id = a.id
LEFT JOIN dingtalk_user u
ON i.user_id = u.userid
WHERE 1=1

  and intermediary_id=#{intermediaryId}


  and intermediary_name LIKE CONCAT('%',#{intermediaryName},'%')


  and i.intermediary_contact LIKE CONCAT('%',#{intermediaryContact},'%')


  and i.intermediary_contact_tel LIKE CONCAT('%',#{intermediaryContactTel},'%')


  and a.name LIKE CONCAT('%',#{area.name},'%')


  and intermediary_logo_url=#{intermediaryLogoUrl}


  and intermediary_create_time=#{intermediaryCreateTime}


  and intermediary_update_time=#{intermediaryUpdateTime}


  and is_plat=#{isPlat}


  and intermediary_introduction=#{intermediaryIntroduction}


  order by ${od}
