mapper.delete:
delete from project
where project_id in

          #{item}

mapper.select:
select  from project where 1=1

  and project_id=#{projectId}


  and project_name=#{projectName}


  and area_id=#{areaId}


  and project_contact_user_id=#{projectContactUserId}


  and project_create_user_id=#{projectCreateUserId}


  and project_owner_contact=#{projectOwnerContact}


  and project_owner_contact_tel=#{projectOwnerContactTel}


  and project_status=#{projectStatus}


  and project_create_time=#{projectCreateTime}


  and project_update_time=#{projectUpdateTime}


  and project_logo=#{projectLogo}


  and project_topic_status=#{projectTopicStatus}


  order by ${od}
