mapper.delete:
delete from wx_material
where material_id in

          #{item}

mapper.select:
select  from wx_material where 1=1

  and material_id=#{materialId}


  and material_media_id=#{materialMediaId}


  and material_type=#{materialType}


  and material_name=#{materialName}


  and material_update_time=#{materialUpdateTime}


  and material_url=#{materialUrl}


  and material_title=#{materialTitle}


  and material_author=#{materialAuthor}


  and material_content_source_url=#{materialContentSourceUrl}


  and material_thumb_media_id=#{materialThumbMediaId}


  and material_show_cover_pic=#{materialShowCoverPic}


  and material_thumb_url=#{materialThumbUrl}


  and material_need_open_comment=#{materialNeedOpenComment}


  and material_only_fans_can_comment=#{materialOnlyFansCanComment}


  and material_digest=#{materialDigest}


  and material_content=#{materialContent}


  order by ${od}
