mapper.delete:
delete from wx_message
where message_id in

          #{item}

mapper.select:
select  from wx_message where 1=1

  and message_id=#{messageId}


  and from_user=#{fromUser}


  and to_user=#{toUser}


  and create_time=#{createTime}


  and msg_type=#{msgType}


  and content=#{content}


  and pic_url=#{picUrl}


  and media_id=#{mediaId}


  and format_=#{format}


  and thumbMedia_id=#{thumbmediaId}


  and location_x=#{locationX}


  and location_y=#{locationY}


  and scale=#{scale}


  and label=#{label}


  and title=#{title}


  and description=#{description}


  and url=#{url}


  and msg_id=#{msgId}


  order by ${od}
