mapper.delete:
delete from wx_user_detail
where wx_user_detail_id in

          #{item}

mapper.select:
select  from wx_user_detail where 1=1

  and wx_user_detail_id=#{wxUserDetailId}


  and subscribe=#{subscribe}


  and openid=#{openid}


  and nickname=#{nickname}


  and sex=#{sex}


  and city=#{city}


  and country=#{country}


  and province=#{province}


  and language=#{language}


  and headimgurl=#{headimgurl}


  and subscribe_time=#{subscribeTime}


  and unionid=#{unionid}


  and remark=#{remark}


  and groupid=#{groupid}


  and subscribe_scene=#{subscribeScene}


  and public_id=#{publicId}


  and intermediary_id=#{intermediaryId}


  order by ${od}
